// Action Creators
export const addUser = (user) => {
    return {
        type: "user/login",
        payload: user,
    }
}

export const removeUser = (id) => {
    return {
        type: "user/logout",
        payload: id,
    }
}

// Reducers
const initialState = {
    username: "Visitor",
    user_type: "visitor",
}

export default function userReducer(state = initialState, action) {
    switch (action.type) {
        case "user/login":
            return {
                username: action.payload.user_name,
                user_type: action.payload.user_type,
            }

        case "user/logout": 
            return {
            username: "Visitor",
            user_type: "visitor",
        }

        default:
            return state;
    }
}
