const initialState = {
    username: "Visitor",
    user_type: "visitor",
}

function userReducer(state = initialState, action) {
    switch (action.type) {
        case "user/login":
            return {
                ...state,
                user_type: action.payload.user_type
            }
        case "user/logout": 
            return {
                ...state,
                 user_type: "visitor"
        }
        default:
            return state;
    }
}

export default userReducer
